/**
 * Process Pinecone Results (metadata-based)
 * Paste this into: Process Pinecone Results2
 */

const pineconeResults = $input.all();

if (!pineconeResults || pineconeResults.length === 0) {
  return [{ json: { success: false, message: "No results from Pinecone" } }];
}

// Helper function to remove only the starting "=" from strings
function cleanValue(value) {
  if (typeof value === "string" && value.startsWith("=")) {
    return value.substring(1);
  }
  return value;
}

const formatted = pineconeResults.map((res, i) => {
  const doc = res.json.document || {};
  const meta = doc.metadata || res.json.metadata || {};

  // Normalize score to 0–100 scale
  let score = res.json.score ?? res.json.similarity ?? 0;
  if (score <= 1) score = score * 100;
  score = Math.max(0, Math.min(100, Number(score)));

  // Clean all metadata fields (except arrays/objects)
  const cleanedMeta = {};
  for (const [key, value] of Object.entries(meta)) {
    if (Array.isArray(value)) {
      cleanedMeta[key] = value.map(v => cleanValue(v));
    } else if (value && typeof value === "object") {
      cleanedMeta[key] = value;
    } else {
      cleanedMeta[key] = cleanValue(value);
    }
  }

  return {
    json: {
      rank: i + 1,
      pineconeScore: Math.round(score * 100) / 100,
      job_id: cleanedMeta.job_id || cleanedMeta.id || "",
      title: cleanedMeta.title || "",
      company: cleanedMeta.company || "",
      location: cleanedMeta.location || "",
      job_type: cleanedMeta.job_type || cleanedMeta.type || "",
      salary: cleanedMeta.salary || "",
      url: cleanedMeta.url || cleanedMeta.link || "",
      posted_date: cleanedMeta.posted_date || cleanedMeta.date_posted || "",
      skills_required: Array.isArray(cleanedMeta.skills_required)
        ? cleanedMeta.skills_required
        : typeof cleanedMeta.skills_required === "string"
        ? cleanedMeta.skills_required
            .split(/[,;|\\n]/)
            .map(s => cleanValue(s.trim()))
            .filter(Boolean)
        : [],
      description: cleanedMeta.description || cleanedMeta.summary || "",
      full_description:
        cleanedMeta.full_description || cleanedMeta.long_description || "",
      source_api: cleanedMeta.source_api || "",
      _rawMeta: meta // keep original metadata for debugging
    }
  };
});

return formatted;

