const jobs = $input.all(); // expects jobs list (likely from Process Pinecone Results or combined)
const resume = $('Code in JavaScript13').first().json.resumeData;

// configuration
const WEIGHTS = { semantic: 0.65, keyword: 0.35 };
const MAX_PINECONE_SCORE = 100; // we normalized to 0-100 earlier

// helper parse job skills
function parseSkills(s) {
  if (!s) return [];
  if (Array.isArray(s)) return s.map(x=>String(x).toLowerCase());
  return String(s).split(/[,;|\\n]/).map(x=>x.toLowerCase().trim()).filter(Boolean);
}
const candidate = (resume.skills || []).map(s => String(s).toLowerCase());

// compute keyword score: % of job required skills matched by candidate (weighted)
function keywordScore(jobSkills, candidateSkills) {
  if (!jobSkills || jobSkills.length === 0) return 0;
  const job = jobSkills.map(s => String(s).toLowerCase());
  let match = 0;
  for (const js of job) {
    for (const cs of candidateSkills) {
      if (cs.includes(js) || js.includes(cs)) { match++; break; }
    }
  }
  // score 0..100
  return Math.round((match / job.length) * 100);
}

// convert semantic to 0..100
function semanticScore(raw) {
  if (!raw) return 0;
  let s = Number(raw);
  if (s <= 1) s = s * 100;
  return Math.round(Math.max(0, Math.min(100, s)));
}

// build outputs with explanation
const scored = jobs.map(j => {
  const job = j.json || {};
  const jobSkills = parseSkills(job.skills_required || job.skills_required || job.description || []);
  const kScore = keywordScore(jobSkills, candidate);
  const sScore = semanticScore(job.pineconeScore || job.semanticScore || 0);

  // penalize big experience mismatch (if requiredExperience in job meta)
  let expPenalty = 0;
  const reqExp = job.requiredExperience || job._filterScore?.requiredExperience || 0;
  if (reqExp) {
    const gap = Math.abs((resume.experience_years || 0) - reqExp);
    if (gap > 3) expPenalty = Math.min(15, (gap - 3) * 5); // small penalty
  }

  const combined = Math.round((sScore * WEIGHTS.semantic + kScore * WEIGHTS.keyword) - expPenalty * 1);

  // which method matched it
  const matchedBy = [];
  if (sScore > 20) matchedBy.push('Vector');
  if (kScore > 10) matchedBy.push('Keyword');

  return {
    json: {
      job_id: job.job_id || '',
      title: job.title || '',
      company: job.company || '',
      location: job.location || '',
      job_type: job.job_type || '',
      url: job.url || '',
      description: job.description || '',
      semanticScore: sScore,
      keywordScore: kScore,
      expPenalty,
      hybridScore: combined,
      matchedBy,
      matchedSkills: jobSkills.filter(js => candidate.some(cs => cs.includes(js) || js.includes(cs))),
      raw: job
    }
  };
});

// sort by hybridScore desc then semanticScore
scored.sort((a,b) => (b.json.hybridScore - a.json.hybridScore) || (b.json.semanticScore - a.json.semanticScore));

// dedupe by job_id keep highest score
const deduped = [];
const seen = new Set();
for (const item of scored) {
  const id = item.json.job_id || item.json.title;
  if (!seen.has(id)) {
    deduped.push(item);
    seen.add(id);
  }
}

// limit top 50 for sheet writes
return deduped.slice(0, 50);

/**
 * Filter Jobs - improved version
 * Paste into node: Code in JavaScript16
 */

const resumeData = $('Code in JavaScript13').first().json.resumeData;
const allJobsFromSheet = $input.all();

// Basic normalizers + small synonyms map
const SYNONYMS = {
  'js': 'javascript',
  'nodejs': 'node.js',
  'c#': 'csharp',
  'py': 'python',
  'aws': 'amazon web services',
  'gcp': 'google cloud platform',
  'ml': 'machine learning',
  'dl': 'deep learning',
  'sde': 'software development engineer'
};

function normalize(s) {
  if (!s) return '';
  return String(s).toLowerCase().trim();
}
function expandToken(token) {
  const t = token.toLowerCase();
  return SYNONYMS[t] || t;
}

// simple levenshtein for fuzzy small tokens
function levenshtein(a, b) {
  if (a === b) return 0;
  if (!a) return b.length;
  if (!b) return a.length;
  const matrix = [];
  for (let i = 0; i <= b.length; i++) {
    matrix[i] = [i];
  }
  for (let j = 0; j <= a.length; j++) {
    matrix[0][j] = j;
  }
  for (let i = 1; i <= b.length; i++) {
    for (let j = 1; j <= a.length; j++) {
      if (b.charAt(i - 1) === a.charAt(j - 1)) {
        matrix[i][j] = matrix[i - 1][j - 1];
      } else {
        matrix[i][j] = Math.min(
          matrix[i - 1][j - 1] + 1,
          Math.min(matrix[i][j - 1] + 1, matrix[i - 1][j] + 1)
        );
      }
    }
  }
  return matrix[b.length][a.length];
}

// build candidate skills normalized + expanded
const candidateSkills = (resumeData.skills || []).map(s => expandToken(normalize(s))).filter(Boolean);

// Create set for fast lookup
const candidateSet = new Set(candidateSkills);

// helper to parse job skills (string or array)
function parseJobSkills(jobSkills) {
  if (!jobSkills) return [];
  if (Array.isArray(jobSkills)) return jobSkills.map(s => expandToken(normalize(s))).filter(Boolean);
  // heuristic split by common separators
  return jobSkills.split(/[,;|\\n]/).map(s => expandToken(normalize(s))).map(s => s.replace(/\\s+/g,' ')).filter(Boolean);
}

// match one skill with fuzzy + exact
function skillMatches(jobSkill, candidateSet) {
  const j = normalize(jobSkill);
  if (candidateSet.has(j)) return true;
  // check token contains or candidate contains job
  for (const c of candidateSet) {
    if (c.includes(j) || j.includes(c)) return true;
    const dist = levenshtein(c, j);
    // allow small typos for short tokens
    if (Math.min(c.length, j.length) <= 6 && dist <= 1) return true;
    if (Math.min(c.length, j.length) > 6 && dist <= 2) return true;
  }
  return false;
}

// domain/title matching with synonyms
function titleMatchesDomain(jobTitle, candidateDomain, candidateExpertise) {
  if (!jobTitle) return false;
  const title = normalize(jobTitle);
  const domain = normalize(candidateDomain || '');
  if (!domain) return true; // no domain given ⇒ accept
  // split into keywords
  const domainTokens = domain.split(/\\s+/).map(expandToken).filter(t=>t.length>2);
  // require at least one strong token present
  for (const dt of domainTokens) {
    if (dt.length <= 3) continue;
    if (title.includes(dt)) return true;
  }
  // check expertise items too
  if (candidateExpertise && Array.isArray(candidateExpertise)) {
    for (const exp of candidateExpertise) {
      if (title.includes(normalize(exp))) return true;
    }
  }
  // fallback: check known synonyms in title
  for (const s in SYNONYMS) {
    if (domain.includes(s) && title.includes(SYNONYMS[s])) return true;
  }
  return false;
}

// filtering loop
const results = [];
const stats = {passed:0, failedDomain:0, failedSkills:0, failedExperience:0};

for (const row of allJobsFromSheet) {
  const j = row.json || {};
  const title = j.title || j['title '] || '';
  const skills = parseJobSkills(j.skills_required || j['skills_required '] || j.description || '');
  const domainOk = titleMatchesDomain(title, resumeData.primary_domain, resumeData.domain_expertise);
  if (!domainOk) { stats.failedDomain++; continue; }

  // count skill matches, prefer core skill hits (if job provides tag "core" not always available)
  let matchCount = 0;
  for (const js of skills) {
    if (skillMatches(js, candidateSet)) matchCount++;
  }
  if (matchCount < 1) { stats.failedSkills++; continue; } // require at least 1 match at first stage

  // experience check (if present in job text)
  const desc = (j.full_description || j.description || '').toLowerCase();
  const expMatch = desc.match(/(\\d+)\\+?\\s*years?/);
  if (expMatch) {
    const reqYears = parseInt(expMatch[1],10);
    const gap = Math.abs((resumeData.experience_years || 0) - reqYears);
    if (gap > 4) { stats.failedExperience++; continue; } // stricter: drop if >4 years gap
  }

  // passed
  stats.passed++;
  results.push({
    json: {
      job_id: j.job_id || j['job_id '] || '',
      title: title,
      company: j.company || j['company '] || '',
      location: j.location || j['location '] || '',
      job_type: j.job_type || '',
      skills_required: skills,
      description: j.description || '',
      full_description: j.full_description || '',
      url: j.url || '',
      posted_date: j.posted_date || j['posted_date '] || '',
      salary: j.salary || '',
      source_api: j.source_api || '',
      _filterScore: { matchCount }
    }
  });
}

// If too few results, relax skill requirement to 1 (we already required 1, if 0 then fallback)
// If still too few (<30), return top N by fuzzy skill overlap across all jobs
if (results.length < 30) {
  // compute fuzzy scores across all jobs and push top up to 100
  const scored = [];
  for (const row of allJobsFromSheet) {
    const j = row.json || {};
    const skills = parseJobSkills(j.skills_required || j.description || '');
    let score = 0;
    for (const js of skills) {
      if (skillMatches(js, candidateSet)) score += 1;
    }
    if (score > 0) {
      scored.push({score, row: j});
    }
  }
  scored.sort((a,b) => b.score - a.score);
  for (const item of scored.slice(0,100)) {
    const j = item.row;
    const already = results.find(r => r.json.job_id === (j.job_id || ''));
    if (!already) {
      results.push({
        json: {
          job_id: j.job_id || '',
          title: j.title || '',
          company: j.company || '',
          location: j.location || '',
          job_type: j.job_type || '',
          skills_required: parseJobSkills(j.skills_required || j.description || ''),
          description: j.description || '',
          full_description: j.full_description || '',
          url: j.url || '',
          posted_date: j.posted_date || '',
          salary: j.salary || '',
          source_api: j.source_api || '',
          _filterScore: { relaxed: true, fuzzyScore: item.score }
        }
      });
    }
  }
}

return results;

